mapper.select:
select rpad(substr(MEM_ID,1, 4), length(mem_id), '*') from member
where mem_phone = #{memPhone} and MEM_EMAIL = #{memEmail} 
and mem_name = #{memName}

mapper.update:
update member
set MEM_EMAIL = #{memEmail}, MEM_EMAIL_CHK = #{memEmailChk},
	MEM_PHONE = null
				#{memPhone},
	MEM_NICK = #{memNick}
where mem_id = #{memId}

update member
set MEM_PW = #{memPw}
where mem_id = #{memId}
